var chatId = msg.payload.chatId;

msg.payload = {
    "chatId" : chatId,
    "type" : "message",
    "content" : String.fromCodePoint(0x1f44b) +
                String.fromCodePoint(0x1f916) + ' Olá!\n\n' +
                "Seja bem vindo ao chatbot de automação de Nível!\n\n" +
                String.fromCodePoint(0x27a1, 0xfe0f) + ` Seu usuário está autorizado para utilizar os controles de automação do ESP32. \n\n` +
                String.fromCodePoint(0x26a0, 0xfe0f) + ' Para maiores informações, acesse o link github do projeto:\n' +
                'https://github.com/FigFelipe/ESP32_Controlador_Nivel_OLED'
}

return msg;

var chatId = msg.payload.chatId;

msg.error = false;

// Dialog
msg.payload.content = String.fromCodePoint(0x26d4) + 
                      '  Usuário(a) não autorizado(a).\n' +
                      '  Solicite autorização ao administrador do bot.';
msg.payload.type = "message";

return  msg;

var chatId = msg.payload.chatId;
var botoes = [];

botoes.push(
    [
        {
            text : "Nome do ESP32",
            callback_data : "/nomeESP"
        },

        {
            text: "Localizar ESP32",
            callback_data: "/localizarESP"
        },

        {
            text: "Definir nível SP",
            callback_data: "/setpointNivel"
        },

        {
            text : "Obter variáveis do processo",
            callback_data : "/variaveis"
        },

        {
            text : "Start",
            callback_data : "/iniciarProcesso"
        },

        {
            text : "Stop",
            callback_data : "/pararProcesso"
        },

        {
            text: "Sair...",
            callback_data: "/sair"
        },
    ]
);

msg.payload = {
    type : "message",
    content : "Por favor, escolha um dos comandos abaixo:",
    chatId : chatId,
    options : {
        parse_mode : "Markdown",
        reply_markup : JSON.stringify({
            remove_keyboard : true,
            inline_keyboard : botoes
        })
    }
};

return msg;

var chatId = msg.payload.chatId;

msg.payload = {
    "chatId" : chatId,
    "type" : "message",
    "content" : String.fromCodePoint(0x1f916) + 
                '  O atendimento foi finalizado. Até a próxima.'
}

return msg;

var chatId = msg.payload.chatId;

msg.payload = {
    "chatId" : chatId,
    "type" : "message",
    "content" : String.fromCodePoint(0x1f916) +
                String.fromCodePoint(0x270f, 0xfe0f) + 
                `  Nome do dispositivo (ESP32):  ${flow.get("clientNomeEsp")}`
}

return msg;

var chatId = msg.payload.chatId;

msg.payload = {
    "chatId" : chatId,
    "type" : "message",
    "content" : String.fromCodePoint(0x1f916) +
                String.fromCodePoint(0x1f50d) + 
                `  Localizando o ESP32 através do LED onboard...`
}

return msg;

var chatId = msg.payload.chatId;

msg.payload = {
    "chatId" : chatId,
    "type" : "message",
    "content" : String.fromCodePoint(0x1f916) +
                String.fromCodePoint(0x2753) +
                `  Qual deverá ser o valor (em Litros) do SetPoint Nível?`
}

return msg;

var regex = /^\d+$/;

if (regex.test(msg.payload.content)) {

    if(msg.payload.content >= 0 && msg.payload.content <= 9999){

        flow.set("isValidoNivelSP", true);
    }

} else {
        flow.set("isValidoNivelSP", false);
}


return msg;

var chatId = msg.payload.chatId;

msg.payload = {
    "chatId" : chatId,
    "type" : "message",
    "content" : String.fromCodePoint(0x1f916) +
                String.fromCodePoint(0x1f197) +
                `  O valor informado de ${msg.payload.content} Litros foi definido para o SetPoint Nível.`
}

return msg;

var chatId = msg.payload.chatId;

msg.payload = {
    "chatId" : chatId,
    "type" : "message",
    "content" : String.fromCodePoint(0x1f916) +
                String.fromCodePoint(0x1f7e2) +
                `  Iniciado o abastecimento do tanque...`
}

return msg;

var chatId = msg.payload.chatId;

msg.payload = {
    "chatId" : chatId,
    "type" : "message",
    "content" : String.fromCodePoint(0x1f916) +
                String.fromCodePoint(0x1f6d1) +
                `  Parando o abastecimento do tanque...`
}

return msg;
